import React, { useState, useContext } from 'react';
import './Checkout.css';
import { ShopContext } from '../../Context/ShopContext';

const Checkout = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const { getTotalCartAmount, all_product, cartItems, checkout } = useContext(ShopContext);

  const products = all_product.filter(product => cartItems[product.product_id] > 0);
  const totalAmount = getTotalCartAmount();

  const handlePayment = async () => {
    try {
      await checkout(phoneNumber, address);
      alert('Order placed successfully!');
    } catch (error) {
      alert('Error during payment');
    }
  };

  return (
    <div className="checkout">
      <h1>Checkout</h1>
      <div className="checkout-container">
        <div className="checkout-form">
          <div className="checkout-form-item">
            <label htmlFor="phone">Phone Number:</label>
            <input 
              type="text" 
              id="phone" 
              value={phoneNumber} 
              onChange={(e) => setPhoneNumber(e.target.value)} 
            />
          </div>
          <div className="checkout-form-item">
            <label htmlFor="address">Shipping Address:</label>
            <input 
              type="text" 
              id="address" 
              value={address} 
              onChange={(e) => setAddress(e.target.value)} 
            />
          </div>
          <button onClick={handlePayment}>Pay</button>
        </div>
        <div className="checkout-summary">
          <h2>Order Summary</h2>
          {products.map((product) => (
            <div key={product.product_id} className="checkout-summary-item">
              <span>{product.product_name}</span>
              <span>Quantity: {cartItems[product.product_id]}</span>
              <span>Price: {product.price} VND </span>
            </div>
          ))}
          <div className="checkout-total">
            <h3>Total Amount: {totalAmount} VND</h3>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
