import React, { createContext, useEffect, useState } from "react";

export const ShopContext = createContext(null);

const getDefaultCart = () => {
  let cart = {};
  for (let index = 0; index < 300 + 1; index++) {
      cart[index] = 0; // Ensure this matches the product_id format
  }
  return cart;
}


const ShopContextProvider = (props) => {
  const [all_product, setAll_Product] = useState([]);
  const [cartItems, setCartItems] = useState(getDefaultCart());

  useEffect(() => {
    fetch('http://localhost:4000/products')
      .then((response) => response.json())
      .then((data) => {
        console.log("Fetched products:", data);
        setAll_Product(data);
      })
      .catch((error) => console.error('Error fetching products:', error));

      fetch('http://localhost:4000/getcart', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'auth-token': `${localStorage.getItem('auth-token')}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({}),
    })
    .then((response) => response.json())
    .then((data) => {
        console.log("Fetched cart data:", data);
        setCartItems(data);
    })
    .catch((error) => console.error('Error fetching cart:', error));
  }, []);

  const addToCart = (productId, quantity) => {
    setCartItems((prevItems) => {
      const updatedItems = {
        ...prevItems,
        [productId]: (prevItems[productId] || 0) + quantity,
      };
      console.log("Updated cart items:", updatedItems);
      return updatedItems;
    });
  };

  const removeFromCart = (productId) => {
    setCartItems((prevItems) => {
      const updatedItems = { ...prevItems };
      delete updatedItems[productId];
      console.log("Removed product:", productId);
      console.log("Updated cart items:", updatedItems);
      return updatedItems;
    });
  };

  const getTotalCartAmount = () => {
    return all_product.reduce((acc, product) => {
      const quantity = cartItems[product.product_id] || 0;
      return acc + (product.price * quantity);
    }, 0);
  };

  const getTotalCartItems = () => {
    return Object.values(cartItems).reduce((acc, quantity) => acc + quantity, 0);
  }

  const contextValue = { getTotalCartItems, getTotalCartAmount, all_product, cartItems, addToCart, removeFromCart };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  )
}

export default ShopContextProvider;
