import React, { useEffect, useState, useContext } from 'react';
import './ProductDisplay.css';
import star_icon from '../Assets/star_icon.png';
import star_dull_icon from '../Assets/star_dull_icon.png';
import { useParams } from 'react-router-dom';
import { ShopContext } from '../../Context/ShopContext';

const ProductDisplay = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [isAdded, setIsAdded] = useState(false);
  const { addToCart } = useContext(ShopContext);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:4000/products/${productId}`);
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleAddToCart = async () => {
    const token = localStorage.getItem('auth-token');
    if (!token) {
      return alert('Vui lòng đăng nhập để thêm sản phẩm vào giỏ hàng');
    }

    try {
      await addToCart(productId, quantity);
      alert('Sản phẩm đã được thêm vào giỏ hàng');
      setIsAdded(true);
    } catch (error) {
      console.error('Lỗi khi thêm vào giỏ hàng:', error);
      alert('Lỗi khi thêm sản phẩm vào giỏ hàng');
    }
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          <img src={`http://localhost:4000/uploads/${product.image_url}`} alt="" />
          <img src={`http://localhost:4000/uploads/${product.image_url}`} alt="" />
          <img src={`http://localhost:4000/uploads/${product.image_url}`} alt="" />
          <img src={`http://localhost:4000/uploads/${product.image_url}`} alt="" />
        </div>
        <div className="productdisplay-img">
          <img className="productdisplay-main-img" src={`http://localhost:4000/uploads/${product.image_url}`} alt="" />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.product_name}</h1>
        <div className="productdisplay-right-stars">
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_dull_icon} alt="" />
          <p>(122)</p>
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-price-old"></div>
          <div className="productdisplay-right-price-new">${product.price}</div>
        </div>
        <div className="productdisplay-right-description">
          HSD:
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Quantity</h1>
          <div className="productdisplay-right-sizes">
            <div className="productdisplay-right-size-btn" onClick={() => setQuantity(prev => Math.max(prev - 1, 1))}>-</div>
            <div className="productdisplay-right-size-value">{quantity}</div>
            <div className="productdisplay-right-size-btn" onClick={() => setQuantity(prev => prev + 1)}>+</div>
          </div>
        </div>
        <button className='productdisplay-right-addtocart' onClick={handleAddToCart}>Add to cart</button>
        {isAdded && <p>Product added to cart!</p>}
      </div>
    </div>
  );
};

export default ProductDisplay;
