import React, { useContext, useEffect, useState } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import { useNavigate } from 'react-router-dom';

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart, updateCartQuantity, applyPromoCode, discount, promoMessage } = useContext(ShopContext);
  const [promoCode, setPromoCode] = useState('');
  const [promoInfo, setPromoInfo] = useState({ usedPromos: [], availablePromos: 0, allPromos: [] });
  const navigate = useNavigate();

  useEffect(() => {
    console.log('Cart items:', cartItems);
  }, [cartItems]);

  useEffect(() => {
    // Gọi API để lấy thông tin mã khuyến mãi
    const fetchPromoInfo = async () => {
      try {
        const userId = localStorage.getItem('user-id'); // Giả sử bạn lưu user-id trong localStorage
        const response = await fetch(`http://localhost:4000/promo-info?user_id=${userId}`);
        const data = await response.json();
        setPromoInfo(data);
      } catch (error) {
        console.error('Error fetching promo info:', error);
      }
    };

    fetchPromoInfo();
  }, []);

  const cartProducts = all_product.filter(product => cartItems[product.product_id] > 0);

  const handleIncreaseQuantity = (productId) => {
    updateCartQuantity(productId, (cartItems[productId] || 0) + 1);
  };

  const handleDecreaseQuantity = (productId) => {
    if (cartItems[productId] > 1) {
      updateCartQuantity(productId, cartItems[productId] - 1);
    }
  };

  const handleApplyPromoCode = () => {
    applyPromoCode(promoCode);
  };

  const totalAmount = getTotalCartAmount();
  const finalAmount = totalAmount - discount;

  return (
    <div className='cartitems'>
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Name</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {cartProducts.length > 0 ? (
        cartProducts.map((product) => (
          <div key={product.product_id}>
            <div className="cartitems-format cartitems-format-main">
              <img src={`http://localhost:4000/uploads/${product.image_url}`} alt="" className='carticon-product-icon' />
              <p>{product.product_name}</p>
              <p>{product.price} VND</p>
              <div className='cartitems-quantity-controls'>
                <button onClick={() => handleDecreaseQuantity(product.product_id)}>-</button>
                <p>{cartItems[product.product_id]}</p>
                <button onClick={() => handleIncreaseQuantity(product.product_id)}>+</button>
              </div>
              <p>{product.price * cartItems[product.product_id]} VND</p>
              <img className='cartitems-remove-icon' src={remove_icon} onClick={() => removeFromCart(product.product_id)} alt="" />
            </div>
            <hr />
          </div>
        ))
      ) : (
        <p>No items in cart.</p>
      )}
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>{totalAmount} VND</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>{finalAmount} VND</h3>
            </div>
          </div>
          <button onClick={() => navigate('/checkout')}>PROCEED TO CHECKOUT</button>
        </div>
        <div className="cartitems-promocode">
          <p>If you have a promo code, enter it here</p>
          <div className="cartitems-promobox">
            <input 
              type="text" 
              placeholder='Promo code' 
              value={promoCode}
              onChange={(e) => setPromoCode(e.target.value)} 
            />
            <button onClick={handleApplyPromoCode}>Submit</button>
          </div>
          {promoMessage && <p className="promo-message">{promoMessage}</p>}
          <div>
            <h4>Available Promo Codes:</h4>
            <ul>
              {promoInfo.allPromos.map(promo => (
                <li key={promo.promo_id}>
                  {promo.promo_code} - {promoInfo.usedPromos.includes(promo.promo_id) ? 'Used' : 'Available'}
                </li>
              ))}
            </ul>
            <p>Total available promo codes: {promoInfo.availablePromos}</p>
          </div>
        </div>
      </div>
    </div>
  )
}

export default CartItems;
