import React, { useState,useContext } from 'react';
import './Checkout.css';
import { ShopContext } from '../../Context/ShopContext';

const Checkout = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const { getTotalCartAmount, all_product, cartItems} = useContext(ShopContext);

  // Example product data
  const products = [
    { name: 'Product 1', quantity: 2, price: 10 },
    { name: 'Product 2', quantity: 1, price: 20 },
    { name: 'Product 3', quantity: 3, price: 5 },
  ];

  const totalAmount = products.reduce((total, product) => total + product.price * product.quantity, 0);

  const handlePayment = () => {
    console.log('Phone Number:', phoneNumber);
    console.log('Address:', address);
  };

  return (
    <div className="checkout">
      <h1>Checkout</h1>
      <div className="checkout-container">
        <div className="checkout-form">
          <div className="checkout-form-item">
            <label htmlFor="phone">Phone Number:</label>
            <input 
              type="text" 
              id="phone" 
              value={phoneNumber} 
              onChange={(e) => setPhoneNumber(e.target.value)} 
            />
          </div>
          <div className="checkout-form-item">
            <label htmlFor="address">Shipping Address:</label>
            <input 
              type="text" 
              id="address" 
              value={address} 
              onChange={(e) => setAddress(e.target.value)} 
            />
          </div>
          <button onClick={handlePayment}>Pay</button>
        </div>
        <div className="checkout-summary">
          <h2>Order Summary</h2>
          {cartItems.map((product, index) => (
            <div key={index} className="checkout-summary-item">
              <span>{product.name}</span>
              <span>Quantity: {product.quantity}</span>
              <span>Price: ${product.price}</span>
            </div>
          ))}
          <div className="checkout-total">
            <h3>Total Amount: ${totalAmount}</h3>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
