import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom'; // Hook để điều hướng
import './Checkout.css';
import { ShopContext } from '../../Context/ShopContext';

const Checkout = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [orderConfirmed, setOrderConfirmed] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('cod'); // Default to 'Cash on Delivery'
  const { getTotalCartAmount, all_product, cartItems, checkout } = useContext(ShopContext);
  const navigate = useNavigate(); // Hook để điều hướng

  const products = all_product.filter(product => cartItems[product.product_id] > 0);
  const totalAmount = getTotalCartAmount();

  const handlePayment = async () => {
    setShowModal(true);
  };

  const confirmOrder = async () => {
    setShowModal(false);
    try {
      await checkout(phoneNumber, address, paymentMethod); // Pass paymentMethod to checkout
      setOrderConfirmed(true);
    } catch (error) {
      alert('Error during payment');
    }
  };

  const cancelOrder = () => {
    setShowModal(false);
  };

  const goBackToCart = () => {
    navigate('/cart'); // Điều hướng về trang giỏ hàng
  };

  if (orderConfirmed) {
    return (
      <div className="order-confirmation-container">
        <div className="order-confirmation">
          <h2>Your order has been confirmed!</h2>
          <p>Thank you for your purchase. You will receive an email confirmation shortly.</p>
          <button onClick={goBackToCart}>Back to Cart</button> {/* Nút quay lại */}
        </div>
      </div>
    );
  }

  return (
    <div className="checkout">
      <h1>Checkout</h1>
      <div className="checkout-container">
        <div className="checkout-form">
          <div className="checkout-form-item">
            <label htmlFor="phone">Phone Number:</label>
            <input 
              type="text" 
              id="phone" 
              value={phoneNumber} 
              onChange={(e) => setPhoneNumber(e.target.value)} 
            />
          </div>
          <div className="checkout-form-item">
            <label htmlFor="address">Shipping Address:</label>
            <input 
              type="text" 
              id="address" 
              value={address} 
              onChange={(e) => setAddress(e.target.value)} 
            />
          </div>
          <div className="checkout-form-item">
  <label htmlFor="deliveryMethod">Delivery Method:</label>
  <div className="radio-group">
    <div>
      <input 
        type="radio" 
        id="online" 
        name="paymentMethod" 
        value="online" 
        checked={paymentMethod === 'online'} 
        onChange={(e) => setPaymentMethod(e.target.value)} 
      />
      <label htmlFor="online">Online Payment</label>
    </div>
    <div>
      <input 
        type="radio" 
        id="cod" 
        name="paymentMethod" 
        value="cod" 
        checked={paymentMethod === 'cod'} 
        onChange={(e) => setPaymentMethod(e.target.value)} 
      />
      <label htmlFor="cod">Cash on Delivery</label>
    </div>
  </div>
</div>

          <button onClick={handlePayment}>Confirm Order</button>
        </div>
        <div className="checkout-summary">
          <h2>Order Summary</h2>
          {products.map((product) => (
            <div key={product.product_id} className="checkout-summary-item">
              <span>{product.product_name}</span>
              <span>Quantity: {cartItems[product.product_id]}</span>
              <span>Price: {product.price} VND</span>
            </div>
          ))}
          <div className="checkout-total">
            <h3>Total Amount: {totalAmount} VND</h3>
          </div>
        </div>
      </div>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Are you sure you want to place this order?</h2>
            <button onClick={confirmOrder}>Yes</button>
            <button onClick={cancelOrder}>No</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Checkout;
