import React, { useContext, useEffect } from 'react';
import './Checkout.css';
import { ShopContext } from '../../Context/ShopContext';

const Checkout = () => {
  const { getTotalCartAmount, getCartItems, cartItems } = useContext(ShopContext);

  useEffect(() => {
    console.log('Cart items:', cartItems);
  }, [cartItems]);

  const cartProducts = getCartItems();

  return (
    <div className='checkout'>
      <div className="checkout-left">
        <h2>Shipping Information</h2>
        <form>
          <label>Phone Number:</label>
          <input type="text" name="phone" required />
          <label>Shipping Address:</label>
          <textarea name="address" required></textarea>
          <button type="submit">Proceed to Payment</button>
        </form>
      </div>
      <div className="checkout-right">
        <h2>Order Summary</h2>
        <div className="checkout-items">
          {cartProducts.map((product) => (
            <div key={product.product_id} className="checkout-item">
              <p>{product.product_name}</p>
              <p>Quantity: {cartItems[product.product_id]}</p>
              <p>Price: ${product.price}</p>
              <p>Total: ${product.price * cartItems[product.product_id]}</p>
            </div>
          ))}
        </div>
        <hr />
        <h3>Total Amount: ${getTotalCartAmount()}</h3>
      </div>
    </div>
  );
}

export default Checkout;
