import React, { useContext } from 'react';
import { ShopContext } from '../../Context/ShopContext';
import './CartItems.css';
import { useNavigate } from 'react-router-dom';

const CartItems = () => {
  const { cartItems, all_product, removeFromCart, updateCartQuantity } = useContext(ShopContext);
  const navigate = useNavigate();

  const handleRemove = async (productId) => {
    try {
      await removeFromCart(productId);
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const handleQuantityChange = async (productId, quantity) => {
    try {
      await updateCartQuantity(productId, quantity);
    } catch (error) {
      console.error('Error updating quantity:', error);
    }
  };

  const getProductDetails = (productId) => {
    return all_product.find(product => product.product_id === productId);
  };

  return (
    <div className='cartitems'>
      <h1>Your Cart</h1>
      <div className='cartitems-list'>
        {Object.keys(cartItems).map(productId => {
          const product = getProductDetails(parseInt(productId));
          const quantity = cartItems[productId];
          if (product) {
            return (
              <div key={productId} className='cartitems-item'>
                <img src={`http://localhost:4000/uploads/${product.image_url}`} alt={product.product_name} />
                <div className='cartitems-details'>
                  <h2>{product.product_name}</h2>
                  <p>Price: ${product.price}</p>
                  <p>Quantity: 
                    <button onClick={() => handleQuantityChange(productId, Math.max(quantity - 1, 1))}>-</button>
                    {quantity}
                    <button onClick={() => handleQuantityChange(productId, quantity + 1)}>+</button>
                  </p>
                  <p>Total: ${(product.price * quantity).toFixed(2)}</p>
                  <button onClick={() => handleRemove(productId)}>Remove</button>
                </div>
              </div>
            );
          }
          return null;
        })}
      </div>
      <button onClick={() => navigate('/checkout')}>Proceed to Checkout</button>
    </div>
  );
};

export default CartItems;
