import React, { useContext } from 'react';
import './CSS/ShopCategory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Components/Assets/dropdown_icon.png';
import banner from '../Components/Assets/freshfood_banner.webp';
import Item from '../Components/Item/Item';

const ShopCategory = (props) => {
  const { all_product, categories } = useContext(ShopContext);

  // Chọn sản phẩm theo category_id
  const filteredProducts = all_product.filter(product => product.category_id === props.category);

  console.log("Filtered products in ShopCategory:", filteredProducts);

  return (
    <div className='shop-category'>
      <img className='shopcategory-banner' src={banner} alt="Banner" />
      <div className="shopcategory-indexSort">
        <p>
          <span>Showing 1-{filteredProducts.length}</span> out of {all_product.length} products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="Sort" />
        </div>
      </div>
      <div className="shopcategory-products">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((item) => (
            <Item
              key={item.product_id}
              product_name={item.product_name}
              image={`http://localhost:4000/uploads/${item.image_url}`}
              price={item.price}
              quantity={item.quantity}
            />
          ))
        ) : (
          <p>No products available</p>
        )}
      </div>
      <div className='shopcategory-loadmore'>
        Explore more
      </div>
    </div>
  );
}

export default ShopCategory;
