import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import './MyOrders.css';

const MyOrders = () => {
    const [orders, setOrders] = useState([]);
    const token = localStorage.getItem('auth-token');
    const history = useHistory(); // Hook để chuyển hướng trang

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await fetch('http://localhost:4000/myorders', {
                    method: 'GET',
                    headers: {
                        'auth-token': token,
                        'Content-Type': 'application/json'
                    }
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setOrders(data);
            } catch (error) {
                console.error('Error fetching orders:', error);
            }
        };

        fetchOrders();
    }, [token]);

    const handleBack = () => {
        history.push('/profile'); // Chuyển hướng về trang Profile
    };

    return (
        <div className="my-orders-container">
            <button className="back-button" onClick={handleBack}>Back to Profile</button>
            <h1 className="my-orders-title">My Orders</h1>
            {orders.length === 0 ? (
                <p className="my-orders-no-orders">No orders found.</p>
            ) : (
                <div className="my-orders-list-container">
                    <ul className="my-orders-list">
                        {orders.map(order => (
                            <li key={order.order_id} className="my-orders-item">
                                <h2 className="my-orders-id">Order ID: {order.order_id}</h2>
                                <p className="my-orders-date">Order Date: {new Date(order.order_date).toLocaleDateString()}</p>
                                <p className="my-orders-amount">Total Amount: ${order.total_amount}</p>
                                <p className="my-orders-status">Status: {order.status}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default MyOrders;
